(function($) {
    const selector = '#custom_slider .swiper';
    let attempts = 0;
    const maxAttempts = 50;

    const checkSwiper = setInterval(function() {
        const $carousel = $(selector);
  const carouselEl = $carousel[0];
  const swiperInstance = carouselEl?.swiper;
        if (swiperInstance) {
            clearInterval(checkSwiper);
            //console.log('¡Swiper detectado!', swiperInstance);
           startCustomCarousel(swiperInstance);
        }
        attempts++;
        if (attempts >= maxAttempts) {
            clearInterval(checkSwiper);
           // console.warn('No se encontró la instancia de Swiper en el tiempo esperado.');
        }
    }, 100);


function startCustomCarousel(slider) {
  if (!slider?.params?.autoplay || !slider.autoplay) return;

  slider.params.autoplay.disableOnInteraction = false;

  const links = document.querySelectorAll('.custom-slider-nav a');
  if (!links.length) return;

  const delay = slider.params.autoplay.delay || 3000;

  function setActiveLink(activeIndex) {
    links.forEach((link, i) => {
      link.classList.toggle('is-active', i === activeIndex);
    });
  }

  function animateProgress(activeIndex) {
    if (!links[activeIndex]) return;

    links.forEach(el => {
      const bar = el.querySelector('.progress');
      if (!bar) return;
      bar.style.transition = 'none';
      bar.style.width = '0';
    });

    setActiveLink(activeIndex);

    const activeBar = links[activeIndex].querySelector('.progress');
    if (!activeBar) return;

    void activeBar.offsetWidth;
    activeBar.style.transition = `width ${delay}ms linear`;
    activeBar.style.width = '100%';
  }

  links.forEach((link, i) => {
    link.addEventListener('click', e => {
      e.preventDefault();
      slider.slideTo(i);
      animateProgress(i);
    });
  });

  slider.on('slideChange', function () {
    animateProgress(this.realIndex);
  });

  animateProgress(0);
  slider.autoplay.start();
}


})(jQuery);
